import React, { Component } from 'react';
import { Media, Player } from 'react-media-player'
import PlayPause from './PlayPause'
import MuteUnmute from './MuteUnmute'
import CurrentTime from './CurrentTime'
import SeekBar from './SeekBar'
import Duration from './Duration'
import Volume from './Volume'
import './songslist.css';
import './App1.css';



export default class Charlie extends Component {
  constructor(props) {
    super(props);
    this.state = {
      songs_list: [],
      song: "http://localhost:5000/get_song?song_name=Arose",
      
    }
  }
  componentDidMount() {
    fetch("http://localhost:5000/charlie_songs")
      .then(response => response.json())
      .then(responseJson => {
        this.setState({
          songs_list: responseJson
        })
      })
  }
  render() {
    return (
      <Media>
        <div >
          <div className="list1" style={{ marginTop: 40,marginLeft: -10}}>

            {this.state.songs_list.map((s) => (
              <ul>
                <button className="semi-transparent-button" style={{ marginLeft: -30, marginTop: 20 }}
                  onClick={() => {
                    this.setState({
                      song: "http://localhost:5000/getcharlie_song?song_name=" + s.name
                    })
                  }}>
                  {s.name}
                </button>
              </ul>
            ))}
          </div>

          <div>
            <Player src={this.state.song} />
          </div>





        </div>

      </Media >

    )
  }
}
