import React, { Component } from 'react';
import ReactDOM from 'react-dom';

class Objectarray extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            arr: []
        }

    }
    getinput(event) {

        this.setState({
            name: event.target.value
        });
    }

    clearAndShow() {
        this.setState({
            name: ""
        })
    }


    btnClicked() {
        this.state.arr.push(this.state.name);
        this.setState({
            arr: this.state.arr


        });
        this.clearAndShow()

        // console.log(this.state.arr);
    }


    render() {
        return (
            <div>
                <ol>
                    {this.state.arr.map((single) => (
                        <div>

                            <li>{single}</li>
                            <button
                                onClick={() => {
                                    this.state.arr.splice(this.state.arr.indexOf(single), 1);
                                    console.log(this.state.arr);
                                    this.setState({
                                      arr:this.state.arr
                                    })

                                }}>
                                Delete
                            </button> 
                           
                        </div>

                    ))}
                     </ol>
                            
                <input type="text" value={this.state.name} onChange={this.getinput.bind(this)} />
                <button onClick={this.btnClicked.bind(this)}>
                    Click Me
              </button>

                {/* <button onClick={this.clearAndShow.bind(this)}>
              clear
              </button> */}
            </div>
        );
    }
}
export default ResetP;
